#include "rle_arr.h"

int strlength(char *str)
{
    int length = 0;
    for (int i = 0; str[i] != '\0'; i++)
    {
        length++;
    }

    if (length > 1)
        return length;
    else
        return 0;
}

// char str[] can be char *str too!
char *rle_array(char str[])
{
    char *p_str = str;
    int pos = 0;
    for (int i = 0; str[i] != '\0'; i++)
    {

        int count = 1;
        if (str[i + 1] != str[i])
        {
            p_str[pos] = str[i];
            pos++;
        }
        if (str[i + 1] == str[i])
        {
            while (str[i + 1] != '\0' && str[i] == str[i + 1])
            {
                count++;
                i++;
            }
            p_str[pos] = '0' + count;
            pos++;
            p_str[pos] = str[i];
            pos++;
        }
    }

    p_str[pos] = '\0';

    return p_str;
}

#ifdef ENABLE_LOCAL_MAIN
#include "rle_arr.h"

char test1[] = "";
char test2[] = "";
char test3[] = "";
char test4[] = "";

int main()
{
    char* compressed = rle_array(test1);
    printf("%s\n",compressed);
    compressed = rle_array(test2);
    printf("%s\n",compressed);
    compressed = rle_array(test3);
    printf("%s\n",compressed);
    compressed = rle_array(test4);
    printf("%s\n",compressed);
    return 0;
}
#endif