#include "rle_arr.h"

int strlength(char *str)
{
    int length = 0;
    for (int i = 0; str[i] != '\0'; i++)
    {
        length++;
    }

    if (length > 1)
        return length;
    else
        return 0;
}

char* rle_array(char str[])
{
    char *p_str = str;
    int pos = 0;
    for (int i = 0; str[i] != '\0'; i++) {

        int count = 1;
        while (str[i + 1] != '\0' && str[i] == str[i + 1]) {
            count++;
            i++;
        }

        p_str[pos] = '0' + count;
        pos++;
        p_str[pos] = str[i];
        pos++;
    }
    //printf("\n");
    return p_str;
}

