#include <stdio.h>

char *rle_v2(char str[])
{
    char *p_str = str;
    int pos = 1;
    
    for (int i = 1; str[i] != '\0'; i++)
    {
        int count = 1;
        while(str[i] == str[i+1] && str[i+1] != '\0')
        {
            count++;
            i++;
        }
        p_str[pos] = '0' + count;
        pos++;
    }

    p_str[pos] = '\0';

    return p_str;
}

